INSERT INTO `t_blog` VALUES ('74', 'centos7.2下安装mysql5.7，使用rpm包安装', '1. 环境本文操作系统: CentOS 7.2.1511 x86_64MySQL 版本: 5.7.162. 卸载系统自带的 mariadb-lib[root@VM_83_130_centos ~]#  rpm -qa|grep mariadbmariadb-libs-5.5.50-1.el7_2.x86_64', '2017-07-27 14:45:47', '00000000000000000107', '<h1><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 29px\">1.&nbsp;</span><strong><span style=\"font-family: 宋体;font-size: 29px\"><span style=\"font-family:宋体\">环境</span></span></strong></h1><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">本文操作系统</span>: CentOS 7.2.1511 x86_64</span></p><p><span style=\";font-family:宋体;font-size:14px\">MySQL <span style=\"font-family:宋体\">版本</span><span style=\"font-family:Calibri\">: 5.7.16</span></span></p><h1><span style=\"font-family: &#39;Times New Roman&#39;;font-size: 29px\">2.&nbsp;</span><strong><span style=\"font-family: 宋体;font-size: 29px\"><span style=\"font-family:宋体\">卸载系统自带的</span> mariadb-lib</span></strong></h1><pre class=\"brush:plain;toolbar:false;\">[root@VM_83_130_centos&nbsp;~]#&nbsp;&nbsp;rpm&nbsp;-qa|grep&nbsp;mariadb\nmariadb-libs-5.5.50-1.el7_2.x86_64\n[root@VM_83_130_centos&nbsp;~]#&nbsp;rpm&nbsp;-e&nbsp;--nodeps&nbsp;mariadb-libs-5.5.50-1.el7_2.x86_64</pre><p><span style=\"font-family: &quot;Times New Roman&quot;; font-size: 29px;\">3.&nbsp;</span><strong><span style=\"font-family: 宋体;font-size: 29px\">下载mysql5.7.16 rpm安装包</span></strong></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">官网最新的</span>mysql<span style=\"font-family:宋体\">安装包是</span><span style=\"font-family:Calibri\">mysql-5.7.16-1.el7.x86_64.rpm-bundle.tar</span><span style=\"font-family:宋体\">下载地址</span></span></p><pre class=\"brush:plain;toolbar:false;\">[root@centos-linux&nbsp;~]#&nbsp;wget&nbsp;http://dev.mysql.com/get/Downloads/MySQL-5.7/mysql-5.7.16-1.el7.x86_64.rpm-bundle.tar\n[root@linux_node_1&nbsp;src]#&nbsp;tar&nbsp;-xvf&nbsp;mysql-5.7.16-1.el7.x86_64.rpm-bundle.tar\n&nbsp;&nbsp;mysql-community-libs-compat-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-devel-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-minimal-debuginfo-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-libs-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-common-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-embedded-compat-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-test-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-embedded-devel-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-server-minimal-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-server-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-client-5.7.16-1.el7.x86_64.rpm\n&nbsp;&nbsp;mysql-community-embedded-5.7.16-1.el7.x86_64.rpm</pre><p><span style=\"font-family: &quot;Times New Roman&quot;; font-size: 29px;\">4.&nbsp;</span><strong><span style=\"font-family: 宋体;font-size: 29px\">安装</span></strong></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">依次执行（几个包有依赖关系，所以执行有先后）下面命令安装</span></span></p><pre class=\"brush:plain;toolbar:false;\">[root@centos-linux&nbsp;~]#&nbsp;rpm&nbsp;-ivh&nbsp;mysql-community-common-5.7.16-1.el7.x86_64.rpm\n[root@centos-linux&nbsp;~]#&nbsp;rpm&nbsp;-ivh&nbsp;mysql-community-libs-5.7.16-1.el7.x86_64.rpm\n[root@centos-linux&nbsp;~]#&nbsp;rpm&nbsp;-ivh&nbsp;mysql-community-client-5.7.16-1.el7.x86_64.rpm\n[root@centos-linux&nbsp;~]#&nbsp;rpm&nbsp;-ivh&nbsp;mysql-community-server-5.7.16-1.el7.x86_64.rpm</pre><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">在安装</span>rpm -ivh mysql-community-server-5.7.16-1.el7.x86_64.rpm<span style=\"font-family:宋体\">的时候报错如下：</span></span></p><pre class=\"brush:plain;toolbar:false;\">warning:&nbsp;mysql-community-server-5.7.16-1.el7.x86_64.rpm:&nbsp;Header&nbsp;V3&nbsp;DSA/SHA1&nbsp;Signature,&nbsp;key&nbsp;ID&nbsp;5072e1f5:&nbsp;NOKEY\nerror:&nbsp;Failed&nbsp;dependencies:\nlibnuma.so.1()(64bit)&nbsp;is&nbsp;needed&nbsp;by&nbsp;mysql-community-server-5.7.16-1.el7.x86_64</pre><p><span style=\"font-family: 宋体; font-size: 14px;\">安装缺失的依赖包</span></p><pre class=\"brush:plain;toolbar:false;\">[root@VM_83_130_centos&nbsp;~]#&nbsp;&nbsp;yum&nbsp;install&nbsp;numactl\n[root@centos-linux&nbsp;~]#&nbsp;rpm&nbsp;-ivh&nbsp;mysql-community-server-5.7.16-1.el7.x86_64.rpm</pre><p><span style=\"font-family: &quot;Times New Roman&quot;; font-size: 29px;\">5.&nbsp;</span><strong><span style=\"font-family: 宋体;font-size: 29px\">数据库初始化</span></strong></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">为了保证数据库目录为与文件的所有者为</span> mysql <span style=\"font-family:宋体\">登陆用户，如果你是以 </span><span style=\"font-family:Calibri\">root </span><span style=\"font-family:宋体\">身份运行 </span><span style=\"font-family:Calibri\">mysql </span><span style=\"font-family:宋体\">服务，需要执行下面的命令初始化</span></span></p><pre class=\"brush:plain;toolbar:false;\">[root@VM_83_130_centos&nbsp;~]#&nbsp;mysqld&nbsp;--initialize&nbsp;--user=mysql</pre><p><span style=\"font-family: 宋体; font-size: 14px;\">如果是以</span><span style=\"font-family: 宋体; font-size: 14px;\"> mysql </span><span style=\"font-family: 宋体; font-size: 14px;\">身份运行，则可以去掉 </span><span style=\"font-size: 14px; font-family: Calibri;\">--user </span><span style=\"font-family: 宋体; font-size: 14px;\">选项。</span></p><p><span style=\";font-family:宋体;font-size:14px\">&nbsp;</span></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">另外</span> --initialize <span style=\"font-family:宋体\">选项默认以“安全”模式来初始化，则会为 </span><span style=\"font-family:Calibri\">root </span><span style=\"font-family:宋体\">用户生成一个密码并将该密码标记为过期，登陆后你需要设置一个新的密码，而使用 </span><span style=\"font-family:Calibri\">--initialize-insecure </span><span style=\"font-family:宋体\">命令则不使用安全模式，则不会为 </span><span style=\"font-family:Calibri\">root </span><span style=\"font-family:宋体\">用户生成一个密码。</span></span></p><p><span style=\";font-family:宋体;font-size:14px\">&nbsp;</span></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">这里演示使用的</span> --initialize <span style=\"font-family:宋体\">初始化的，会生成一个 </span><span style=\"font-family:Calibri\">root </span><span style=\"font-family:宋体\">账户密码，密码在</span><span style=\"font-family:Calibri\">log</span><span style=\"font-family:宋体\">文件里，红色区域的就是自动生成的密码</span></span></p><pre class=\"brush:plain;toolbar:false;\">[root@linux_node_1&nbsp;src]#&nbsp;cat&nbsp;/var/log/mysqld.log&nbsp;\n&nbsp;&nbsp;2016-12-07T04:41:58.028558Z&nbsp;0&nbsp;[Warning]&nbsp;TIMESTAMP&nbsp;with&nbsp;implicit&nbsp;DEFAULT&nbsp;value&nbsp;is&nbsp;deprecated.&nbsp;Please&nbsp;use&nbsp;--explicit_defaults_for_timestamp&nbsp;server&nbsp;option&nbsp;(see&nbsp;documentation&nbsp;for&nbsp;more&nbsp;details).\n&nbsp;&nbsp;2016-12-07T04:41:58.315188Z&nbsp;0&nbsp;[Warning]&nbsp;InnoDB:&nbsp;New&nbsp;log&nbsp;files&nbsp;created,&nbsp;LSN=45790\n&nbsp;&nbsp;2016-12-07T04:41:58.355114Z&nbsp;0&nbsp;[Warning]&nbsp;InnoDB:&nbsp;Creating&nbsp;foreign&nbsp;key&nbsp;constraint&nbsp;system&nbsp;tables.\n&nbsp;&nbsp;2016-12-07T04:41:58.418448Z&nbsp;0&nbsp;[Warning]&nbsp;No&nbsp;existing&nbsp;UUID&nbsp;has&nbsp;been&nbsp;found,&nbsp;so&nbsp;we&nbsp;assume&nbsp;that&nbsp;this&nbsp;is&nbsp;the&nbsp;first&nbsp;time&nbsp;that&nbsp;this&nbsp;server&nbsp;has&nbsp;been&nbsp;started.&nbsp;Generating&nbsp;a&nbsp;new&nbsp;UUID:&nbsp;7cc98831-bc37-11e6-a263-000c29f902dc.\n&nbsp;&nbsp;2016-12-07T04:41:58.419935Z&nbsp;0&nbsp;[Warning]&nbsp;Gtid&nbsp;table&nbsp;is&nbsp;not&nbsp;ready&nbsp;to&nbsp;be&nbsp;used.&nbsp;Table&nbsp;&#39;mysql.gtid_executed&#39;&nbsp;cannot&nbsp;be&nbsp;opened.\n&nbsp;&nbsp;2016-12-07T04:41:58.420558Z&nbsp;1&nbsp;[Note]&nbsp;A&nbsp;temporary&nbsp;password&nbsp;is&nbsp;generated&nbsp;for&nbsp;root@localhost:&nbsp;)1r3gi,hjgQa</pre><p><span style=\"font-family: 宋体; font-size: 14px;\">现在启动</span><span style=\"font-family: 宋体; font-size: 14px;\">mysql</span><span style=\"font-family: 宋体; font-size: 14px;\">数据库</span></p><pre class=\"brush:plain;toolbar:false;\">[root@linux_node_1&nbsp;src]#&nbsp;systemctl&nbsp;start&nbsp;mysqld.service\n[root@linux_node_1&nbsp;src]#&nbsp;mysql&nbsp;-uroot&nbsp;-p</pre><p><span style=\"font-family: &quot;Times New Roman&quot;; font-size: 29px;\">6</span><span style=\"font-family: &quot;Times New Roman&quot;; font-size: 29px;\">.&nbsp;</span><strong><span style=\"font-family: 宋体;font-size: 29px\">忘记root密码<span style=\"font-family:Times New Roman\">,</span>重置</span></strong></p><pre class=\"brush:as3;toolbar:false\">vi&nbsp;/etc/my.cnf</pre><p></p><p>在[mysqld]中添加</p><pre class=\"brush:as3;toolbar:false\">skip-grant-tables</pre><p></p><p>重启mysql</p><pre class=\"brush:as3;toolbar:false\">systemctl&nbsp;restart&nbsp;mysqld.service</pre><p></p><p>用户无密码登录</p><pre class=\"brush:cpp;toolbar:false\">mysql&nbsp;-uroot&nbsp;-p&nbsp;(直接点击回车，密码为空)</pre><p></p><p>修改</p><p></p><pre class=\"brush:css;toolbar:false\">use&nbsp;mysql;\nupdate&nbsp;user&nbsp;set&nbsp;authentication_string=password(&#39;123456&#39;)&nbsp;where&nbsp;user=&#39;root&#39;;\nflush&nbsp;privileges;</pre><p></p><p>删除my.cnf中的skip-grant-tables,并重启mysql</p><pre class=\"brush:cf;toolbar:false\">service&nbsp;mysql&nbsp;restart</pre><p></p><p><span style=\"font-family: &quot;Times New Roman&quot;; font-size: 29px;\">7.&nbsp;</span><strong><span style=\"font-family: 宋体;font-size: 29px\">修改密码</span></strong><br/></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">该密码被标记为过期了，如果想正常使用还需要修改密码</span></span></p><pre class=\"brush:plain;toolbar:false;\">mysql&gt;&nbsp;show&nbsp;databases;\nERROR&nbsp;1820&nbsp;(HY000):&nbsp;You&nbsp;must&nbsp;reset&nbsp;your&nbsp;password&nbsp;using&nbsp;ALTER&nbsp;USER&nbsp;statement&nbsp;before&nbsp;executing&nbsp;this&nbsp;statement.</pre><p><span style=\"font-family: 宋体; font-size: 14px;\">以前的</span><span style=\"font-family: 宋体; font-size: 14px;\"> password()</span><span style=\"font-family: 宋体; font-size: 14px;\">函数将会被抛弃，官方建议使用下面的命令来修改密码</span></p><pre class=\"brush:plain;toolbar:false;\">mysql&gt;&nbsp;ALTER&nbsp;USER&nbsp;&#39;root&#39;@&#39;localhost&#39;&nbsp;IDENTIFIED&nbsp;BY&nbsp;&#39;new_password&#39;;</pre><p><span style=\"font-family: &quot;Times New Roman&quot;; font-size: 29px;\">8.&nbsp;</span><strong><span style=\"font-family: 宋体;font-size: 29px\">设置远程连接</span></strong></p><pre class=\"brush:plain;toolbar:false;\">mysql&gt;&nbsp;grant&nbsp;all&nbsp;privileges&nbsp;on&nbsp;*.*&nbsp;to&nbsp;&#39;root&#39;@&#39;%&#39;&nbsp;identified&nbsp;by&nbsp;&#39;123456&#39;&nbsp;with&nbsp;grant&nbsp;option;\nmysql&gt;&nbsp;flush&nbsp;privileges;</pre><p><span style=\"font-family: &quot;Times New Roman&quot;; font-size: 29px;\">9.&nbsp;</span><strong><span style=\"font-family: 宋体;font-size: 29px\">修改配置</span></strong></p><h2><span style=\"font-family: Arial;font-size: 21px\">9.1.&nbsp;</span><strong><span style=\"font-family: 黑体;font-size: 21px\"><span style=\"font-family:黑体\">运行博客项目时抛出异常</span></span></strong></h2><pre class=\"brush:plain;toolbar:false;\">###&nbsp;Cause:&nbsp;com.mysql.jdbc.exceptions.jdbc4.MySQLSyntaxErrorException:&nbsp;Expression&nbsp;#1&nbsp;of&nbsp;SELECT&nbsp;list&nbsp;is&nbsp;not&nbsp;in\n&nbsp;GROUP&nbsp;BY&nbsp;clause&nbsp;and&nbsp;contains&nbsp;nonaggregated&nbsp;column&nbsp;&#39;blog3.t2.id&#39;&nbsp;\n&nbsp;which&nbsp;is&nbsp;not&nbsp;functionally&nbsp;dependent&nbsp;on&nbsp;columns&nbsp;in&nbsp;GROUP&nbsp;BY&nbsp;clause;&nbsp;this&nbsp;is&nbsp;incompatible&nbsp;with&nbsp;\n&nbsp;sql_mode=only_full_group_by;&nbsp;bad&nbsp;SQL&nbsp;grammar&nbsp;[];&nbsp;nested&nbsp;exception&nbsp;is&nbsp;com.mysql.jdbc.exceptions.jdbc4.MySQLSyntaxErrorException:&nbsp;Expression&nbsp;#1&nbsp;of&nbsp;\n&nbsp;SELECT&nbsp;list&nbsp;is&nbsp;not&nbsp;in&nbsp;GROUP&nbsp;BY&nbsp;clause&nbsp;and&nbsp;contains&nbsp;nonaggregated&nbsp;column&nbsp;&#39;blog3.t2.id&#39;&nbsp;which&nbsp;is&nbsp;not&nbsp;functionally&nbsp;dependent&nbsp;on&nbsp;columns&nbsp;in&nbsp;GROUP&nbsp;BY&nbsp;clause;&nbsp;\n&nbsp;this&nbsp;is&nbsp;incompatible&nbsp;with&nbsp;sql_mode=only_full_group_by\nat&nbsp;org.springframework.jdbc.support.SQLExceptionSubclassTranslator.doTranslate(SQLExceptionSubclassTranslator.java:91)\nat&nbsp;org.springframework.jdbc.support.AbstractFallbackSQLExceptionTranslator.translate(AbstractFallbackSQLExceptionTranslator.java:73)\nat&nbsp;org.springframework.jdbc.support.AbstractFallbackSQLExceptionTranslator.translate(AbstractFallbackSQLExceptionTranslator.java:81)\nat&nbsp;org.mybatis.spring.MyBatisExceptionTranslator.translateExceptionIfPossible(MyBatisExceptionTranslator.java:74)\nat&nbsp;org.mybatis.spring.SqlSessionTemplate$SqlSessionInterceptor.invoke(SqlSessionTemplate.java:399)\nat&nbsp;com.sun.proxy.$Proxy11.selectList(Unknown&nbsp;Source)</pre><p><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">输入下面即可</span></span></p><pre class=\"brush:plain;toolbar:false;\">SET&nbsp;@@GLOBAL.sql_mode=&quot;STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION&quot;;</pre><p><br/></p>', '13', '安装 mysql');
INSERT INTO `t_blog` VALUES ('75', 'centos7安装ss服务端与bbr算法', '1、安装ShadowSocks[root@localhost ~]# yum install python-setuptools && easy_install pip [root@localhost ~]# pip install shadowsocks2、创建配置文件/etc/shadowsocks.json[root@localhost /]# touch /etc/shadowsocks.', '2017-08-18 15:00:58', '00000000000000000078', '<p>1、安装ShadowSocks<br/></p><pre class=\"brush:bash;toolbar:false\">[root@localhost&nbsp;~]#&nbsp;yum&nbsp;install&nbsp;python-setuptools&nbsp;&amp;&amp;&nbsp;easy_install&nbsp;pip&nbsp;\n[root@localhost&nbsp;~]#&nbsp;pip&nbsp;install&nbsp;shadowsocks</pre>2、创建配置文件/etc/shadowsocks.json[root@localhost&nbsp;/]#&nbsp;touch&nbsp;/etc/shadowsocks.json<br/><pre class=\"brush:bash;toolbar:false\">[root@localhost&nbsp;/]#&nbsp;vi&nbsp;/etc/shadowsocks.json\n{\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;server&quot;:&quot;0.0.0.0&quot;,\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;server_port&quot;:443,\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;local_address&quot;:&nbsp;&quot;127.0.0.1&quot;,\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;local_port&quot;:1080,\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;password&quot;:&quot;MyPass&quot;,\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;timeout&quot;:600,\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;method&quot;:&quot;rc4-md5&quot;\n}</pre><p>备注：加密方式官方默认使用aes-256-cfb，推荐使用rc4-md5，因为 RC4比AES速度快好几倍。 各字段说明：</p><pre class=\"brush:bash;toolbar:false\">server:服务器IP\nserver_port:服务器端口\nlocal_port:本地端端口\npassword:用来加密的密码\ntimeout:超时时间（秒）\nmethod:加密方法，可选择&nbsp;“bf-cfb”,&nbsp;“aes-256-cfb”,&nbsp;“des-cfb”,&nbsp;“rc4″等</pre><p>3、使用配置文件在后台运行shadowsocks服务</p><pre class=\"brush:bash;toolbar:false\">[root@localhost&nbsp;/]#&nbsp;ssserver&nbsp;-c&nbsp;/etc/shadowsocks.json&nbsp;-d&nbsp;start</pre><p>4、停止服务<br/></p><pre class=\"brush:bash;toolbar:false\">[root@localhost&nbsp;/]#&nbsp;ssserver&nbsp;-c&nbsp;/etc/shadowsocks.json&nbsp;-d&nbsp;stop</pre><p>5.&nbsp; 关闭防火墙<br/></p><pre class=\"brush:bash;toolbar:false\">systemctl&nbsp;stop&nbsp;firewalld.service&nbsp;#停止firewall\nsystemctl&nbsp;disable&nbsp;firewalld.service&nbsp;#禁止firewall开机启动</pre>6.1.&nbsp;极路由安装商店的ss<p>进入智能插件中心，随便点一个插件，然后将浏览器地址栏中最后面的sid=后面的数字改为163116535，然后回车，就可以装官方的SS插件了</p><p>6.2.&nbsp;极路由安装第三方ss<br/></p><pre class=\"brush:cpp;toolbar:false\">cd&nbsp;/tmp&nbsp;&amp;&amp;&nbsp;curl&nbsp;-k&nbsp;-o&nbsp;hiwifios1.0.sh&nbsp;https://sspanel.tabboa.com/hiwifios1.0.sh&nbsp;&amp;&amp;&nbsp;sh&nbsp;hiwifios1.0.sh</pre><p>7.&nbsp;一键脚本安装bbr</p><p>本脚本适用环境</p><p>系统支持：CentOS 6+，Debian 7+，Ubuntu 12+<br/></p><p>虚拟技术：OpenVZ 以外的，比如 KVM、Xen、VMware 等</p><p>本脚本已在&nbsp;Vultr&nbsp;上的 VPS 全部测试通过。</p><p><br/></p><p>使用root用户登录，运行以下命令：</p><pre class=\"brush:css;toolbar:false\">wget&nbsp;--no-check-certificate&nbsp;https://github.com/teddysun/across/raw/master/bbr.sh&nbsp;&amp;&amp;&nbsp;chmod&nbsp;+x&nbsp;bbr.sh&nbsp;&amp;&amp;&nbsp;./bbr.sh</pre><p>安装完成后，脚本会提示需要重启 VPS，输入 y 并回车后重启。<br/></p><p>重启完成后，进入 VPS，验证一下是否成功安装最新内核并开启 TCP BBR，输入以下命令：</p><pre class=\"brush:cpp;toolbar:false\">uname&nbsp;-r</pre><p>查看内核版本，含有 4.13 就表示 OK 了</p><pre class=\"brush:bash;toolbar:false\">sysctl&nbsp;net.ipv4.tcp_available_congestion_control</pre><p>返回值一般为：<br/></p><p>net.ipv4.tcp_available_congestion_control = bbr cubic reno</p><pre class=\"brush:cpp;toolbar:false\">sysctl&nbsp;net.ipv4.tcp_congestion_control</pre><p>返回值一般为：</p><p>net.ipv4.tcp_congestion_control = bbr</p><pre class=\"brush:css;toolbar:false\">sysctl&nbsp;net.core.default_qdisc</pre><p>返回值一般为：</p><p>net.core.default_qdisc = fq</p><pre class=\"brush:css;toolbar:false\">lsmod&nbsp;|&nbsp;grep&nbsp;bbr</pre><p>返回值有 tcp_bbr 模块即说明 bbr 已启动。注意：并不是所有的 VPS 都会有此返回值，若没有也属正常。</p><p><br/></p>', '5', 'ss');
INSERT INTO `t_blog` VALUES ('76', '[转]小白科普：悲观锁和乐观锁', '旺财和小强生活在一个网上商城的系统中， 是一对儿线程好基友。 星期一刚上班，旺财接到领导电话说，要把一个商品的库存减少20， 旺财不敢怠慢，赶快把库存取出来一看，哦，现在有1000个。与此同时，小强也接到电话说要把同一商品的库存减少30， 他一看，哦，现在有1000个。旺财计算出最新的库存值980， 保存！小强也计算出最新的库存值970， 保存 !  旺财的数据被小强覆盖了！领导一看，本来卖出了5', '2017-09-22 10:13:31', '00000000000000000034', '<p>旺财和小强生活在一个网上商城的系统中， 是一对儿线程好基友。&nbsp;</p><p><br/></p><p>星期一刚上班，旺财接到领导电话说，要把一个商品的库存减少20， 旺财不敢怠慢，赶快把库存取出来一看，哦，现在有1000个。</p><p><br/></p><p>与此同时，小强也接到电话说要把同一商品的库存减少30， 他一看，哦，现在有1000个。</p><p><br/></p><p>旺财计算出最新的库存值980， 保存！</p><p><br/></p><p>小强也计算出最新的库存值970， 保存 ! &nbsp;</p><p><br/></p><p>旺财的数据被小强覆盖了！</p><p><br/></p><p>领导一看，本来卖出了50个商品，现在库存只扣了30个，这样持续下去就天下大乱了。&nbsp;</p><p><br/></p><p>旺财和小强， 各打二十大板， 长长记性！</p><p><br/></p><p>小强说：“哥，要不我们还是想个办法吧，再这样下去要被打死的。”</p><p><br/></p><p>旺财悲催地说： “这样， 以后我们每次访问库存之前，都要先加锁，加了锁，就禁止别人再进入访问，只能等待持有锁的人来释放。”</p><p><br/></p><p>星期二， 领导让旺财再次把库存减少20 ， 旺财这次万分小心，先把库存给锁住，然后慢慢修改。&nbsp;</p><p><br/></p><p>小强也接到了把库存减少的指令， 但是旺财哥已经把库存锁住了， 不能操作，小强只好去阻塞车间喝茶聊天，然后到就绪车间等待调度运行。&nbsp;</p><p><br/></p><p>好不容易等到可以再次执行了，小强一看，这库存怎么还锁着呢！？ 只好再次去阻塞车间喝茶。&nbsp;</p><p><br/></p><p>领导一看， 小强你怎么回事， 老是喝茶聊天？ ！&nbsp; 还干不干活了？</p><p><br/></p><p>小强争辩说旺财哥一直锁着库存，我没法操作。</p><p><br/></p><p>领导不管这些， 把小强和旺财又打了二十大板。&nbsp;</p><p><br/></p><p>（备注： 这种加锁的方式就是悲观锁了，悲观锁正如其名，每次取读写数据时候总认为数据会被别人修改，所以将数据加锁，置于锁定状态， 不让别人再访问。缺点是如果持有锁的时间太长，其他用户需要等待很长时间。）</p><p><br/></p><p>旺财说： “兄弟，这一次哥对不住你啊，处理得慢了一些， 不过哥刚才挨打的时候想了一个好办法：乐观锁。”</p><p><br/></p><p>小强说：“拉倒吧你，屁股都快被打烂了还乐观？”</p><p><br/></p><p>“你听我说嘛， 我们在那个库存字段的旁边，再加上一个版本(version)的字段， 例如刚开始的时候（库存= 1000， 版本=1）， 每次你去读的时候不仅要读出库存，还要读出版本号， 等到你修改了库存，往回写的时候一定要检查一下版本号，看看和你读的时候是否一样。”</p><p><br/></p><p>“如果不一样呢？”&nbsp; 小强问</p><p><br/></p><p>“那就放弃这次写的操作，重新读取库存和版本号， 重新来过。”</p><p><br/></p><p>“如果一样呢？ ”</p><p><br/></p><p>“那就放心大胆地把新的库存值写回去。把版本号也加1”</p><p><br/></p><p>“我似乎有点明白了，我们试试，不过你要想好，我可不想再挨板子了。”</p><p><br/></p><p>星期三， 旺财奉命把库存减去30， 他先读到了（库存= 1000， 版本=1）；&nbsp; 小强也要改库存了，他要把库存减去50， 于是他也读到了（库存= 1000， 版本=1）。&nbsp;</p><p><br/></p><p>旺财计算出新的库存值970 ，写回成功，现在版本变成了（库存= 970， 版本=2）。&nbsp;</p><p><br/></p><p>小强也计算出新库存950 ， 也准备写回，他战战兢兢地去看最新的版本号， 已经变成版本2了， 按照之前的约定，只好重新读取了。</p><p><br/></p><p>小强再次读取（库存= 970， 版本=2） ， 计算出最新缓存值920（970减去50）， 再次试图更新，没想到又被别人抢了先，现在版本号已经变成3了 ，最新的数据是（库存= 900， 版本= 3）。</p><p><br/></p><p>唉， 只好重新来过， 计算出最新缓存值850（900减去50），第三次终于更新成功了， 最新的库存是 （库存=850， 版本= 4）</p><p><br/></p><p>领导看到旺财和小强忙得热火朝天，一刻不停，并且库存值也没有乱掉， 满意地点了点头：好同志啊。</p><p><br/></p><p>（备注： 这种方式就是所谓的乐观锁了，旺财和小强这次乐观了一点，觉得一般情况下不会有太多人修改库存，所以没有加锁，放心地去操作，只有在最后更新的时候才去看是否冲突。 这种方式适合于冲突不多的场景，如果冲突很多，数据争用激烈，会导致不断地尝试，反而降低了性能。）</p><p><br/></p><p>来源:微信公众号码农翻身</p><p><br/></p>', '2', 'data-options=\"required:true\"');
INSERT INTO `t_blog` VALUES ('77', '我的实用chrome浏览器插件', 'dark reader : 让页面颜色反色python shell : chrome中的python交互程序web前端助手 : 提供字符串编码解码,代码美化,二维码生成等功能apidebug : 接口测试插件', '2017-10-18 14:04:19', '00000000000000000046', '<p>dark&nbsp;reader : 让页面颜色反色<br/></p><p>python&nbsp;shell : chrome中的python交互程序</p><p>web前端助手 : 提供字符串编码解码,代码美化,二维码生成等功能</p><p>apidebug : 接口测试插件<br/></p>', '5', 'chrome 插件');
INSERT INTO `t_blog` VALUES ('78', '启用Win8/10(中文版/核心版/家庭版)中被阉割的远程桌面服务端', 'Windows 8/8.1/10 标准版(中文版/核心版/家庭版)中取消了远程桌面服务端，想通过远程连接到自己的电脑就很麻烦了，第三方远程桌面速度又不理想(如TeamViewer)。通过以下方法可让系统恢复远程桌面功能。国内多数品牌机都是预装的win8/10中文版 下载安装一个自动化工具即可，不需要懂太多专业知识。下载地址：https://github.com/binarymaster/rdpwr', '2017-11-22 14:15:49', '00000000000000000023', '<p><span style=\"color: rgb(69, 69, 69); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.92px; background-color: rgb(255, 255, 255);\"></span>Windows 8/8.1/10 标准版(中文版/核心版/家庭版)中取消了远程桌面服务端，想通过远程连接到自己的电脑就很麻烦了，第三方远程桌面速度又不理想(如TeamViewer)。</p><p>通过以下方法可让系统恢复远程桌面功能。国内多数品牌机都是预装的win8/10中文版 下载安装一个自动化工具即可，不需要懂太多专业知识。</p><p>下载地址：<a href=\"https://github.com/binarymaster/rdpwrap/releases\" target=\"_blank\" title=\"https://github.com/binarymaster/rdpwrap/releases\">https://github.com/binarymaster/rdpwrap/releases</a></p><p><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.92px; background-color: rgb(255, 255, 255); color: rgb(51, 102, 255);\"><a href=\"http://img.blog.csdn.net/20161017155844795?watermark/2/text/aHR0cDovL2Jsb2cuY3Nkbi5uZXQv/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70/gravity/Center\"><img src=\"http://img.blog.csdn.net/20161017155844795?watermark/2/text/aHR0cDovL2Jsb2cuY3Nkbi5uZXQv/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70/gravity/Center\" class=\"img-responsive\"/></a></span></p>', '5', '远程桌面');
INSERT INTO `t_blog` VALUES ('79', '树莓派3b安装lede(openwrt分支)打造成智能路由器', '1.下载lede 17.01.4   http://downloads.lede-project.org/releases/17.01.4/targets/brcm2708/bcm2710/lede-17.01.4-brcm2708-bcm2710-rpi-3-ext4-sdcard.img.gz2. 解压出lede-17.01.4-brcm2708-bcm2710-rpi-3-ext4-sdca', '2017-12-13 14:11:17', '00000000000000000042', '<p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">1.下载lede 17.01.4&nbsp; &nbsp;</span><a href=\"http://downloads.lede-project.org/releases/17.01.4/targets/brcm2708/bcm2710/lede-17.01.4-brcm2708-bcm2710-rpi-3-ext4-sdcard.img.gz\"><span style=\"text-decoration:underline;\"><span style=\"color: rgb(0, 0, 255);letter-spacing: 0;font-size: 16px\">http://downloads.lede-project.org/releases/17.01.4/targets/brcm2708/bcm2710/lede-17.01.4-brcm2708-bcm2710-rpi-3-ext4-sdcard.img.gz</span></span></a></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">2. 解压出lede-17.01.4-brcm2708-bcm2710-rpi-3-ext4-sdcard.img</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">3.linux&nbsp;下通过dd命令烧录到sd卡中,先进入img所在的文件目录,并且执行命令</span></p><pre class=\"brush:plain;toolbar:false\">dd&nbsp;if=lede-17.01.4-brcm2708-bcm2710-rpi-3-ext4-sdcard.img&nbsp;of=/dev/mmcblk0</pre><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">4.通过网线连接电脑,默认ip为192.168.1.1,通过浏览器可以访问</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">5.用xshell&nbsp;ssh访问192.168.1.1:22</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">6.修改/etc/config/network文件，配置wan口和lan口如下,网线接口变为dhcp客户端</span></p><pre class=\"brush:plain;toolbar:false;\">..........\nconfig&nbsp;interface&nbsp;&#39;lan&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;type&nbsp;&#39;bridge&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;proto&nbsp;&#39;static&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;ipaddr&nbsp;&#39;192.168.2.1&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;netmask&nbsp;&#39;255.255.255.0&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;ip6assign&nbsp;&#39;60&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;gateway&nbsp;&#39;192.168.2.1&#39;\nconfig&nbsp;interface&nbsp;&#39;wan&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;proto&nbsp;&#39;dhcp&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;ifname&nbsp;&#39;eth0</pre><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">7.配置Openwrt无线网络，修改/etc/config/wireless文件，配置wlan0的模式、ssid、key等信息，如下</span></p><pre class=\"brush:plain;toolbar:false;\">..........\nconfig&nbsp;wifi-iface\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;device&nbsp;&#39;radio0&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;network&nbsp;&#39;lan&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;mode&nbsp;&#39;ap&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;ssid&nbsp;&#39;openwrt&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;encryption&nbsp;&#39;psk2&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;key&nbsp;&#39;123456&#39;</pre><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">8.配置允许通过wan连接ssh，修改/etc/config/firewall文件,开放22和80端口,用于ssh和网页访问</span></p><pre class=\"brush:bash;toolbar:false\">..........\nconfig&nbsp;rule&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;src&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;wan\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;dest_port&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;22\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;target&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ACCEPT&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;proto\nconfig&nbsp;rule&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;src&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;wan\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;dest_port&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;80\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;target&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ACCEPT&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;option&nbsp;proto</pre><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">9.重启树莓派,然后用网线连上可以上网的路由器,就能上网了</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">10.电脑连接树莓派的wifi,ssh连接192.168.2.1:22,升级官方源</span></p><pre class=\"brush:bash;toolbar:false\">opkg&nbsp;update</pre><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">11.浏览器访问192.168.2.1 ,&nbsp;点击system-&gt;software,就可以下载许多好用的软件了</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;比如shadowsocks用于fq,aria2和transmission的下载,adblock广告屏蔽</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\"></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">&nbsp;</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">12.&nbsp;解决aria2下载百度云连接失败</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\"><span style=\"font-family:sans-serif\">在启动项中加入</span>&nbsp; &nbsp;check-certificate=false</span></p><p><br/></p>', '14', '树莓派');
INSERT INTO `t_blog` VALUES ('80', 'openwrt安装KodExplorer', 'KodExplorer可道云在线文档管理器· 几分钟内搭建您的专属私有云盘· 轻松向客户/公司内部员工分享文件· 完全支持私有化部署，自主可控· 有浏览器，就可以轻松登录和管理文档· 数百种文件格式的在线预览、编辑和播放https://github.com/kalcaddle/KodExplorer/ 0.从github上下载代码,放到路由器上,我放在了移动硬盘上,挂载的位置是/mnt/sda1/', '2018-01-02 23:53:05', '00000000000000000034', '<p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">KodExplorer可道云在线文档管理器</span></p><p style=\"margin-top:7px;margin-bottom:7px;margin-left:24px;margin-top:auto;margin-bottom:auto\"><span style=\"font-family:Symbol;font-size:13px\">·&nbsp;</span><span style=\"letter-spacing: 0px;\">几分钟内搭建您的专属私有云盘</span></p><p style=\"margin-top:7px;margin-bottom:7px;margin-left:24px;margin-top:auto;margin-bottom:auto\"><span style=\"font-family:Symbol;font-size:13px\">·&nbsp;</span><span style=\"letter-spacing: 0px;\">轻松向客户</span><span style=\"letter-spacing: 0px;\">/公司内部员工分享文件</span></p><p style=\"margin-top:7px;margin-bottom:7px;margin-left:24px;margin-top:auto;margin-bottom:auto\"><span style=\"font-family:Symbol;font-size:13px\">·&nbsp;</span><span style=\"letter-spacing: 0px;\">完全支持私有化部署，自主可控</span></p><p style=\"margin-top:7px;margin-bottom:7px;margin-left:24px;margin-top:auto;margin-bottom:auto\"><span style=\"font-family:Symbol;font-size:13px\">·&nbsp;</span><span style=\"letter-spacing: 0px;\">有浏览器，就可以轻松登录和管理文档</span></p><p style=\"margin-top:7px;margin-bottom:7px;margin-left:24px;margin-top:auto;margin-bottom:auto\"><span style=\"font-family:Symbol;font-size:13px\">·&nbsp;</span><span style=\"letter-spacing: 0px;\">数百种文件格式的在线预览、编辑和播放</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><a href=\"https://github.com/kalcaddle/KodExplorer/\" title=\"https://github.com/kalcaddle/KodExplorer/\"><span style=\"text-decoration:underline;\"><span style=\"color: rgb(0, 0, 255);letter-spacing: 0;font-size: 16px\">https://github.com/kalcaddle/KodExplorer/</span></span></a></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">&nbsp;</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">0.从github上下载代码,放到路由器上,我放在了移动硬盘上,挂载的位置是/mnt/sda1/kod</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">1.安装php与nginx</span></p><pre class=\"brush:bash;toolbar:false\">opkg&nbsp;install&nbsp;nginx&nbsp;php7&nbsp;php7-mod-gd&nbsp;php7-mod-session&nbsp;php7-mod-pdo&nbsp;php7-mod-mcrypt&nbsp;php7-mod-mbstring&nbsp;php7-fastcgi&nbsp;php7-cgi&nbsp;php7-mod-xml&nbsp;php7-mod-ctype&nbsp;php7-mod-curl&nbsp;php7-mod-exif&nbsp;php7-mod-ftp&nbsp;php7-mod-iconv&nbsp;php7-mod-json&nbsp;php7-mod-sockets&nbsp;php7-mod-sqlite3&nbsp;php7-mod-tokenizer&nbsp;spawn-fcgi&nbsp;php7-mod-zip</pre><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">2.逐行修改/etc/php.ini</span></p><pre class=\"brush:plain;toolbar:false\">short_open_tag&nbsp;=&nbsp;on&nbsp;\ndoc_root&nbsp;=&nbsp;&quot;/mnt/sda1/kod&quot;&nbsp;&nbsp;&nbsp;#设置kodexplorer的根目录位置\nextension_dir&nbsp;=&nbsp;&quot;/usr/lib/php&quot;</pre><span style=\"letter-spacing: 0px;\">3.修改/etc/nginx/nginx.conf的server代码段</span><pre class=\"brush:plain;toolbar:false\">server&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;listen&nbsp;81;\n&nbsp;&nbsp;server_name&nbsp;&nbsp;localhost;\n&nbsp;&nbsp;#charset&nbsp;koi8-r;\n&nbsp;&nbsp;#access_log&nbsp;&nbsp;logs/host.access.log&nbsp;&nbsp;main;\n&nbsp;&nbsp;&nbsp;&nbsp;root&nbsp;&nbsp;/mnt/sda1/kod;\n&nbsp;&nbsp;index&nbsp;&nbsp;index.html&nbsp;index.htm&nbsp;index.php&nbsp;default.php;\n&nbsp;&nbsp;location&nbsp;~*&nbsp;^.+\\.php(\\/.*)*$&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fastcgi_pass&nbsp;&nbsp;&nbsp;127.0.0.1:1026;\n&nbsp;&nbsp;&nbsp;&nbsp;fastcgi_index&nbsp;&nbsp;index.php;\n&nbsp;&nbsp;&nbsp;&nbsp;fastcgi_param&nbsp;&nbsp;SCRIPT_FILENAME&nbsp;&nbsp;/mnt/sda1/kod/$fastcgi_script_name;\n&nbsp;&nbsp;&nbsp;&nbsp;include&nbsp;&nbsp;&nbsp;&nbsp;fastcgi_params;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n}</pre><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\"><span style=\"font-family:sans-serif\">其中</span>81是访问时的端口, /mnt/sda1/kod是下载下来的代码的根目录,根据自己实际情况修改</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">4.启动spawn-fcgi</span></p><pre class=\"brush:bash;toolbar:false\">/usr/bin/spawn-fcgi&nbsp;-a&nbsp;127.0.0.1&nbsp;-p&nbsp;1026&nbsp;-C&nbsp;2&nbsp;-f&nbsp;/usr/bin/php-cgi</pre><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\"><span style=\"font-family:sans-serif\">这里的默认端口是</span>1026，要跟/etc/nginx/nginx.conf中设置的端口对应</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">5.启动nginx</span></p><pre class=\"brush:bash;toolbar:false\">/etc/init.d/nginx&nbsp;start</pre><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">6.访问路由器的ip:81就可以进入了</span></p><p><span style=\";font-family:Calibri;font-size:14px\">&nbsp;</span></p><p><br/></p>', '14', '路由器');
INSERT INTO `t_blog` VALUES ('81', 'cookie跨二级域名共享', 'Cookie二级域名跨域需要设置:1.setDomain，设置一级域名：    .porkchop.cn    .baidu.com2.setPath。设置为“/”获得域名的方法/** * 获得当前网站的域名,不包含端口 */private String getDomain(HttpServletReques', '2018-01-31 19:18:43', '00000000000000000012', '<p>Cookie二级域名跨域需要设置:</p><p>1.setDomain，设置一级域名：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;.porkchop.cn</p><p>&nbsp;&nbsp;&nbsp;&nbsp;.baidu.com</p><p>2.setPath。设置为“/”</p><p><br/></p><p>获得域名的方法</p><pre class=\"brush:java;toolbar:false\">/**\n&nbsp;*&nbsp;获得当前网站的域名,不包含端口\n&nbsp;*/\nprivate&nbsp;String&nbsp;getDomain(HttpServletRequest&nbsp;request)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;url&nbsp;=&nbsp;request.getRequestURL().toString();\n&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;temp&nbsp;=&nbsp;url.substring(url.indexOf(&quot;:&quot;)&nbsp;+&nbsp;3);\n&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;domainWithPort&nbsp;=&nbsp;temp.substring(0,&nbsp;temp.indexOf(&quot;/&quot;));\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;!domainWithPort.contains(&quot;:&quot;)&nbsp;?domainWithPort:domainWithPort.substring(0,domainWithPort.indexOf(&quot;:&quot;));\n}</pre><p>设置cookie跨域<br/></p><pre class=\"brush:java;toolbar:false\">Cookie&nbsp;cookie&nbsp;=&nbsp;new&nbsp;Cookie(&quot;key&quot;,&nbsp;&quot;value&quot;);\n//设置共享cookie的域名\nif(!&quot;localhost&quot;.equals(domain)){\n&nbsp;&nbsp;&nbsp;//设置的值为&quot;.porkchop.cn&quot;\n&nbsp;&nbsp;&nbsp;cookie.setDomain(domain.substring(domain.indexOf(&quot;.&quot;)));\n}\ncookie.setPath(&quot;/&quot;);\nresponse.addCookie(cookie);</pre><p><br/></p>', '1', 'cookie 跨域 单点登录');
INSERT INTO `t_blog` VALUES ('82', 'tomcat配置let\'s encrypt的免费ssl证书', '1.使用appnode的let\'s encrypt面板生成证书,生成的位置在/opt/appnode/agent/apps/letsencrypt/data/账户名/2.进入上面的目录,里面包含.crt和.key文件    我的是blog.porkchop.cn.crt和blog.porkchop.cn.key3.合并证书，生成temp.p12文件openssl pkcs12 -export -i', '2018-01-31 19:48:54', '00000000000000000014', '<p>1.使用appnode的let&#39;s&nbsp;encrypt面板生成证书,生成的位置在/opt/appnode/agent/apps/letsencrypt/data/账户名/</p><p>2.进入上面的目录,里面包含.crt和.key文件</p><p>&nbsp;&nbsp;&nbsp;&nbsp;我的是blog.porkchop.cn.crt和blog.porkchop.cn.key</p><p>3.合并证书，生成temp.p12文件<br/></p><pre class=\"brush:bash;toolbar:false\">openssl&nbsp;pkcs12&nbsp;-export&nbsp;-in&nbsp;blog.porkchop.cn.crt&nbsp;-inkey&nbsp;blog.porkchop.cn.key&nbsp;-out&nbsp;temp.p12&nbsp;-name&nbsp;temp</pre>4.生成 keystore 文件(jks) 需要输入上一步的导出密码，及指定新的 keystore 密码&nbsp;&nbsp; 后面几步的导入需要用到这个密码。<pre class=\"brush:as3;toolbar:false\">keytool&nbsp;-importkeystore&nbsp;-srckeystore&nbsp;temp.p12&nbsp;-srcstoretype&nbsp;PKCS12&nbsp;-destkeystore&nbsp;temp.jks</pre>5.修改tomcat/conf/server.xml<pre class=\"brush:xml;toolbar:false\">&lt;Connector&nbsp;port=&quot;80&quot;&nbsp;protocol=&quot;HTTP/1.1&quot;\n&nbsp;&nbsp;&nbsp;connectionTimeout=&quot;20000&quot;\n&nbsp;&nbsp;redirectPort=&quot;443&quot;&nbsp;/&gt;\n&nbsp;&nbsp;\n&lt;Connector&nbsp;port=&quot;443&quot;&nbsp;protocol=&quot;HTTP/1.1&quot;&nbsp;&nbsp;\n&nbsp;&nbsp;SSLEnabled=&quot;true&quot;&nbsp;maxThreads=&quot;500&quot;&nbsp;scheme=&quot;https&quot;\n&nbsp;&nbsp;secure=&quot;true&quot;&nbsp;clientAuth=&quot;false&quot;&nbsp;sslProtocol=&quot;TLS&quot;\n&nbsp;&nbsp;keystoreFile=&quot;/.jks文件的路径/temp.jks&quot;&nbsp;keystorePass=&quot;上面设置的密码&quot;&nbsp;/&gt;\n&nbsp;&nbsp;\n&lt;Connector&nbsp;port=&quot;8009&quot;&nbsp;protocol=&quot;AJP/1.3&quot;&nbsp;redirectPort=&quot;443&quot;&nbsp;/&gt;</pre><p>主要是配置端口,把默认的8443改为443</p>6.设置Http自动跳转Https,修改tomcat/conf/web.xml,在末尾添加以下代码<pre class=\"brush:xml;toolbar:false\">&lt;security-constraint&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;web-resource-collection&nbsp;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;web-resource-name&nbsp;&gt;SSL&lt;/web-resource-name&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;url-pattern&gt;/*&lt;/url-pattern&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/web-resource-collection&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;user-data-constraint&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;transport-guarantee&gt;CONFIDENTIAL&lt;/transport-guarantee&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/user-data-constraint&gt;\n&lt;/security-constraint&gt;</pre><p>7.启动tomcat</p><p>输入http://blog.porkcho.cn就会自动跳转到https://blog.porkchop.cn</p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p>', '13', 'https tomcat');
INSERT INTO `t_blog` VALUES ('83', 'redis整合spring的实用工具类', '只定义了部分方法,其他需要的方法可以继续添加1.定义jedis各种方法的接口import java.util.List;public interface JedisClient {   String set(String key, String value);   String get(String key);   Boolean exists(String key);   Long expire', '2018-02-02 13:15:29', '00000000000000000050', '<p>只定义了部分方法,其他需要的方法可以继续添加</p><p>1.定义jedis各种方法的接口</p><pre class=\"brush:java;toolbar:false\">import&nbsp;java.util.List;\n\npublic&nbsp;interface&nbsp;JedisClient&nbsp;{\n&nbsp;&nbsp;&nbsp;String&nbsp;set(String&nbsp;key,&nbsp;String&nbsp;value);\n&nbsp;&nbsp;&nbsp;String&nbsp;get(String&nbsp;key);\n&nbsp;&nbsp;&nbsp;Boolean&nbsp;exists(String&nbsp;key);\n&nbsp;&nbsp;&nbsp;Long&nbsp;expire(String&nbsp;key,&nbsp;int&nbsp;seconds);\n&nbsp;&nbsp;&nbsp;Long&nbsp;ttl(String&nbsp;key);\n&nbsp;&nbsp;&nbsp;Long&nbsp;incr(String&nbsp;key);\n&nbsp;&nbsp;&nbsp;Long&nbsp;hset(String&nbsp;key,&nbsp;String&nbsp;field,&nbsp;String&nbsp;value);\n&nbsp;&nbsp;&nbsp;String&nbsp;hget(String&nbsp;key,&nbsp;String&nbsp;field);\n&nbsp;&nbsp;&nbsp;Long&nbsp;hdel(String&nbsp;key,&nbsp;String...&nbsp;field);\n&nbsp;&nbsp;&nbsp;Boolean&nbsp;hexists(String&nbsp;key,&nbsp;String&nbsp;field);\n&nbsp;&nbsp;&nbsp;List&lt;String&gt;&nbsp;hvals(String&nbsp;key);\n&nbsp;&nbsp;&nbsp;Long&nbsp;del(String&nbsp;key);\n}</pre><p>2.单机版使用连接池的实现类<br/></p><pre class=\"brush:java;toolbar:false\">import&nbsp;java.util.List;\n\nimport&nbsp;redis.clients.jedis.Jedis;\nimport&nbsp;redis.clients.jedis.JedisPool;\n\npublic&nbsp;class&nbsp;JedisClientPool&nbsp;implements&nbsp;JedisClient&nbsp;{\n\n&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;JedisPool&nbsp;jedisPool;\n\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;JedisPool&nbsp;getJedisPool()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisPool;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;void&nbsp;setJedisPool(JedisPool&nbsp;jedisPool)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.jedisPool&nbsp;=&nbsp;jedisPool;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;set(String&nbsp;key,&nbsp;String&nbsp;value)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;result&nbsp;=&nbsp;jedis.set(key,&nbsp;value);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;get(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;result&nbsp;=&nbsp;jedis.get(key);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;Boolean&nbsp;exists(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Boolean&nbsp;result&nbsp;=&nbsp;jedis.exists(key);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;expire(String&nbsp;key,&nbsp;int&nbsp;seconds)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Long&nbsp;result&nbsp;=&nbsp;jedis.expire(key,&nbsp;seconds);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;ttl(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Long&nbsp;result&nbsp;=&nbsp;jedis.ttl(key);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;incr(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Long&nbsp;result&nbsp;=&nbsp;jedis.incr(key);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;hset(String&nbsp;key,&nbsp;String&nbsp;field,&nbsp;String&nbsp;value)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Long&nbsp;result&nbsp;=&nbsp;jedis.hset(key,&nbsp;field,&nbsp;value);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;hget(String&nbsp;key,&nbsp;String&nbsp;field)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;result&nbsp;=&nbsp;jedis.hget(key,&nbsp;field);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;hdel(String&nbsp;key,&nbsp;String...&nbsp;field)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Long&nbsp;result&nbsp;=&nbsp;jedis.hdel(key,&nbsp;field);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;Boolean&nbsp;hexists(String&nbsp;key,&nbsp;String&nbsp;field)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Boolean&nbsp;result&nbsp;=&nbsp;jedis.hexists(key,&nbsp;field);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;List&lt;String&gt;&nbsp;hvals(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;List&lt;String&gt;&nbsp;result&nbsp;=&nbsp;jedis.hvals(key);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;del(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Jedis&nbsp;jedis&nbsp;=&nbsp;jedisPool.getResource();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Long&nbsp;result&nbsp;=&nbsp;jedis.del(key);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jedis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n}</pre><p>3.集群版的实现类</p><pre class=\"brush:java;toolbar:false\">import&nbsp;java.util.List;\n\nimport&nbsp;redis.clients.jedis.JedisCluster;\n\npublic&nbsp;class&nbsp;JedisClientCluster&nbsp;implements&nbsp;JedisClient&nbsp;{\n&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;private&nbsp;JedisCluster&nbsp;jedisCluster;\n&nbsp;&nbsp;&nbsp;\n\n&nbsp;&nbsp;&nbsp;public&nbsp;JedisCluster&nbsp;getJedisCluster()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster;\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;public&nbsp;void&nbsp;setJedisCluster(JedisCluster&nbsp;jedisCluster)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.jedisCluster&nbsp;=&nbsp;jedisCluster;\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;set(String&nbsp;key,&nbsp;String&nbsp;value)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.set(key,&nbsp;value);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;get(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.get(key);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;Boolean&nbsp;exists(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.exists(key);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;expire(String&nbsp;key,&nbsp;int&nbsp;seconds)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.expire(key,&nbsp;seconds);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;ttl(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.ttl(key);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;incr(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.incr(key);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;hset(String&nbsp;key,&nbsp;String&nbsp;field,&nbsp;String&nbsp;value)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.hset(key,&nbsp;field,&nbsp;value);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;hget(String&nbsp;key,&nbsp;String&nbsp;field)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.hget(key,&nbsp;field);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;hdel(String&nbsp;key,&nbsp;String...&nbsp;field)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.hdel(key,&nbsp;field);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;Boolean&nbsp;hexists(String&nbsp;key,&nbsp;String&nbsp;field)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.hexists(key,&nbsp;field);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;List&lt;String&gt;&nbsp;hvals(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.hvals(key);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;Long&nbsp;del(String&nbsp;key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;jedisCluster.del(key);\n&nbsp;&nbsp;&nbsp;}\n\n}</pre><p>4.spring中的配置</p><p>注意,如果按照类型注入,集群版和单机版只能共存一个</p><pre class=\"brush:xml;toolbar:false\">&lt;!--&nbsp;配置单机版的连接&nbsp;--&gt;\n&lt;!--&lt;bean&nbsp;id=&quot;jedisPool&quot;&nbsp;class=&quot;redis.clients.jedis.JedisPool&quot;&gt;--&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--&lt;constructor-arg&nbsp;name=&quot;host&quot;&nbsp;value=&quot;119.28.142.5&quot;&gt;&lt;/constructor-arg&gt;--&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--&lt;constructor-arg&nbsp;name=&quot;port&quot;&nbsp;value=&quot;6379&quot;&gt;&lt;/constructor-arg&gt;--&gt;\n&lt;!--&lt;/bean&gt;--&gt;\n&lt;!--&lt;bean&nbsp;class=&quot;cn.porkchop.ebuy.jedis.JedisClientPool&quot;&gt;--&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--&lt;property&nbsp;name=&quot;jedisPool&quot;&nbsp;ref=&quot;jedisPool&quot;/&gt;--&gt;\n&lt;!--&lt;/bean&gt;--&gt;\n\n&lt;!--&nbsp;集群版的配置&nbsp;--&gt;\n&lt;bean&nbsp;id=&quot;jedisCluster&quot;&nbsp;class=&quot;redis.clients.jedis.JedisCluster&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;set&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;bean&nbsp;class=&quot;redis.clients.jedis.HostAndPort&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;host&quot;&nbsp;value=&quot;119.28.142.5&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;port&quot;&nbsp;value=&quot;7001&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/bean&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;bean&nbsp;class=&quot;redis.clients.jedis.HostAndPort&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;host&quot;&nbsp;value=&quot;119.28.142.5&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;port&quot;&nbsp;value=&quot;7002&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/bean&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;bean&nbsp;class=&quot;redis.clients.jedis.HostAndPort&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;host&quot;&nbsp;value=&quot;119.28.142.5&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;port&quot;&nbsp;value=&quot;7003&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/bean&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;bean&nbsp;class=&quot;redis.clients.jedis.HostAndPort&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;host&quot;&nbsp;value=&quot;119.28.142.5&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;port&quot;&nbsp;value=&quot;7004&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/bean&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;bean&nbsp;class=&quot;redis.clients.jedis.HostAndPort&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;host&quot;&nbsp;value=&quot;119.28.142.5&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;port&quot;&nbsp;value=&quot;7005&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/bean&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;bean&nbsp;class=&quot;redis.clients.jedis.HostAndPort&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;host&quot;&nbsp;value=&quot;119.28.142.5&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;constructor-arg&nbsp;name=&quot;port&quot;&nbsp;value=&quot;7006&quot;/&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/bean&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/set&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/constructor-arg&gt;\n&lt;/bean&gt;\n&lt;bean&nbsp;class=&quot;cn.porkchop.ebuy.jedis.JedisClientCluster&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;property&nbsp;name=&quot;jedisCluster&quot;&nbsp;ref=&quot;jedisCluster&quot;/&gt;\n&lt;/bean&gt;</pre><p>5.service层代码<br/></p><pre class=\"brush:java;toolbar:false\">@Service\npublic&nbsp;class&nbsp;ServiceImpl&nbsp;implements&nbsp;Service&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;@Autowired\n&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;JedisClient&nbsp;cluster;\n........\n.........\n}</pre><p><br/></p>', '1', 'redis');

